import React, { useState, } from 'react';
import API from "../utils/API";
import { Button, Card } from 'react-bootstrap';

const [cartState, setCartState] = useState({
  image: 10000,
  name: "Chase",
  saleprice: "excited",
  link: "Alec",
  reviews: "5 stars"
});

let itemsincart = [];
API.loadCart(itemsincart)
console.log(itemsincart);


function Cart() {
  setCartState({
    name: itemsincart.name,
    image: itemsincart.image,
    saleprice: itemsincart.salePrice,
    link: itemsincart.link,
    reviews: itemsincart.reviews
  
  })
  return (
    <React.Fragment> 
      <center><h1>Cart Page</h1></center>

      <Card style={{ width: '18rem' }}>
          <Card.Img variant="top" src="{image}" />
          <Card.Body>
            <Card.Title>{cartState.name}</Card.Title>
            <Card.Text>{cartState.saleprice}</Card.Text>
            <Button variant="primary" href={cartState.link}>Item Link</Button>
          </Card.Body>
        </Card>

    </React.Fragment>
   );
}

export default Cart;
