import React, { useState, } from 'react';
import API from "../utils/API";
import { Button, Card } from 'react-bootstrap';

const [itemState, setItemState] = useState({
  image: 10000,
  name: "Chase",
  saleprice: "excited",
  link: "Alec",
  reviews: "5 stars"
});

let itemsincart = [];
API.loadCart(itemsincart)
console.log(itemsincart);
setItemState({
  name: itemsincart.name,
  image: itemsincart.image,
  saleprice: itemsincart.salePrice,
  link: itemsincart.url,
  reviews: itemsincart.customerReviewAverage

})

function Cart() {
  return (
    <React.Fragment> 
      <center><h1>Cart Page</h1></center>

      <Card style={{ width: '18rem' }}>
          <Card.Img variant="top" src="{image}" />
          <Card.Body>
            <Card.Title>{itemState.name}</Card.Title>
            <Card.Text>{itemState.saleprice}</Card.Text>
            <Button variant="primary" href={itemState.link}>Item Link</Button>
          </Card.Body>
        </Card>

    </React.Fragment>
   );
}

export default Cart;
