import React, { useRef } from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import FormControl from 'react-bootstrap/FormControl';
import API from "../utils/API";


const centertext = {
  textAlign: "center"
};
const centercontent = {
  alignContent: "center"
}
const row = {
  backgroundColor: 'lightblue',
  height: 'auto',
};
/*const picture = {
  padding: '5px',
  textAlign: 'center',
  marginLeft: '30px'

};*/
const text = {
  textAlign: 'center',
  padding: '25px'

}

function Home() {

  const state = {
    items: []
};

function componentDidMount() {
    API.loadItems()
        .then(res => this.setState({ 
          items: res
        }))
        .catch(err => console.log(err));
        console.log(res)
}

  let texInput = useRef();
  const handleFormSubmit = (event) => {
    event.preventDefault();
    let thingsearched="";
    thingsearched = texInput.current.value

    console.log(thingsearched)

    let image;
    let name;
    let price;
    let link;
    let reviews;
    API.findAll(thingsearched)
        .then(res => {
            /*if (res.data.status === "error") {
                throw new Error(res.data.message);
            }*/
            console.log(res)
            //console.log(res[0].name)
            //name = res[0].name;
            //this.setState({ users: res.data.results, error: "" });
        })
        //.catch(err => this.setState({ error: err.message }));
  };


  return (
    <React.Fragment>
      <h1 style={centertext}>Home Page</h1>
      <Form inline style={centercontent}>
        <FormControl ref={texInput} type="text" placeholder="Search something..." className="mr-sm-2" />
        <button onClick={handleFormSubmit}>submit</button>
      </Form>
      <Container style={row}>
        <Row>
          <Col style={text}>image</Col>
          <Col style={text}>name</Col>
          <Col style={text}>saleprice</Col>
          <Col style={text}>link</Col>
          <Col style={text}>reviews</Col>
        </Row>
        <Row>
          <Col style={text}>image</Col>
          <Col style={text}>name</Col>
          <Col style={text}>saleprice</Col>
          <Col style={text}>link</Col>
          <Col style={text}>reviews</Col>
        </Row>
      </Container>
    </React.Fragment>
  );
}

export default Home;
