import React, { useState } from 'react';
import API from "../utils/API";
import { Button, Card } from 'react-bootstrap';

const state = {
  itemsincart : []
}

function Cart() {
  const [itemState, setItemState] = useState({
    image: 10000,
    name: "Chase",
    saleprice: "excited",
    link: "Alec",
    reviews: "5 stars"
  });

  API.loadCart(itemsincart)
   .then(itemsincart => {

  // setItemState({
  //   name: itemsincart.name,
  //   image: itemsincart.image,
  //   saleprice: itemsincart.salePrice,
  //   link: itemsincart.link,
  //   reviews: itemsincart.reviews
  // })
   })
  return (
    <React.Fragment>
      <center><h1>Cart Page</h1></center>

      {state.itemsincart.map(item => (
                    <Container style={row}>
                        <Row>
                            <Col style={picture}><Img src={item.picture.large} /></Col>
                            <Col style={text}>{item.name.first} {item.name.last}</Col>
                            <Col style={text}>{item.cell}</Col>
                            <Col style={text}>{item.email}</Col>
                            <Col style={text}>{item.dob.date}</Col>
                        </Row>
                    </Container>
                ))}

      <Card style={{ width: '18rem' }}>
        <Card.Img variant="top" src="{image}" />
        <Card.Body>
          <Card.Title>{itemState.name}</Card.Title>
          <Card.Text>{itemState.saleprice}</Card.Text>
          <Button variant="primary" href={itemState.link}>Item Link</Button>
        </Card.Body>
      </Card>

    </React.Fragment>
  );
}

export default Cart;
